import React from 'react';
import classNames from 'classnames';
import useFetch from 'hooks/useFetch';

const AddToFavorites = ({isFavorited,favoritesCount,articleSlug}) => {
  const apiUrl = `/articles/${articleSlug}/favorite`;
  const [{response}, doFetch] = useFetch(apiUrl);
  console.log("AddToFavorites -> response", response)
  const isFaovriteWidthResponse = response ? response.article.favorited : isFavorited;
  const favoriteCountWithResponse = response ? response.article.favoritesCount : favoritesCount;
  const buttonClasses = classNames({
    btn: true,
    'btn-sm': true,
    'btn-primary': isFaovriteWidthResponse,
    'btn-outline-primary': !isFaovriteWidthResponse
  })
  const handleLike = event => {
    event.preventDefault();
    doFetch({
      method: isFaovriteWidthResponse ? 'delete' : 'post'
    })
  }
  return (
    <button className={buttonClasses} onClick={handleLike}>
      <i className="ion-heart"></i>
      <span>&nbsp; {favoriteCountWithResponse}</span>
    </button>  
  )
}

export default AddToFavorites;