import React, { useEffect, Fragment } from 'react';
import {stringify} from 'query-string';
import Feed from 'components/feed';
import useFetch from 'hooks/useFetch';
import Pagination from 'components/pagination';
import {getPagination, limit} from 'utils';
import PopularTags from 'components/popularTags';
import Loading from 'components/loading';
import ErrorMessage from 'components/errorMessage';

const GlobalFeed = ({location,match}) => {
  const {offset, currentPage} = getPagination(location.search);
  const stringtifiedParams = stringify({
    limit,
    offset
  })
  const apiUrl = `/articles?${stringtifiedParams}`;
  const currentUrl = match.url;
  console.log("GlobalFeed -> currentUrl", currentUrl)
  const [{response, error, isLoading}, doFetch] = useFetch(apiUrl);
  // console.log('res', response, error, isLoading);

  useEffect(() => {
    doFetch()
  },[currentPage, doFetch])

  return(
    <div className="home-page">
      <div className="banner">
        <h1>Medium Clone</h1>
        <p>A place to share knowledge</p>
      </div>
      <div className="container page">
        <div className="row">
          <div className="col-md-9">
            {isLoading && <Loading />}
            {error && <ErrorMessage />}
            {!isLoading && response &&(
              <Fragment>
                <Feed articles={response.articles}/>
                <Pagination 
                  total={response.articlesCount} 
                  limit={limit} 
                  url={currentUrl} 
                  currentPage={currentPage} 
                />
              </Fragment>
            )}
          </div>
          <div className="col-md-3">
            <PopularTags />
          </div>
        </div>
      </div>
    </div>
  ) 
}

export default GlobalFeed;